import React, {useState} from 'react';
import { render } from 'react-dom';
import './App.css';
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";

const PAGE_PRODUCTS = 'products';
const PAGE_CART = 'cart';

function App() {

  const[Cart, setCart] = useState([]);
  const[page, setPage] = useState(PAGE_PRODUCTS);

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  function validateForm() {
    return email.length > 0 && password.length > 0;
  }

  function handleSubmit(event) {
    event.preventDefault();
  }



  const [product] = useState([
    {
      name:'Laptop',
      cost:'£299.99',
      image:'https://brain-images-ssl.cdn.dixons.com/4/2/10199624/u_10199624.jpg'
    },

    {
      name:'PC Mouse',
      cost:'£15.00',
      image:'https://media.4rgos.it/s/Argos/9213513_R_SET?w=270&h=270&qlt=75&fmt=webp'
    },
    {
      name:'PC Keyboard',
      cost:'£80.00',
      image:'https://i.pcmag.com/imagery/reviews/03IEAmirU1dayDyI0jwMNnP-4.1569475096.fit_scale.size_1182x667.jpg'
    }
  ]);

  const addToCart = (product) => {
    setCart([...Cart, {...product}]);
  }

  const removeFromCart = (productToRemove) =>{
    setCart(Cart.filter(product => product !== productToRemove));
  }

  const navigateTo = (nextPage)=>{
    setPage(nextPage);
  }

  const renderProducts = () => (
    <>
    <h1>Products</h1>
        <div className="products">
          {product.map((product, idx) => (
            <div className="product" key={idx}>
              <h3>{product.name}</h3>
              <h4>{product.cost}</h4>
              <img src={product.image} alt={product.name}/>
              <button onClick={() => addToCart(product)}>Add to Cart</button>
            </div>
          ))}
      </div>
      </>
  )

  const renderCart = () => (
    <>
    <h1>Cart</h1>
        <div className="cart">
          {Cart.map((product, idx) => (
            <div className="product" key={idx}>
              <h3>{product.name}</h3>
              <h4>{product.cost}</h4>
              <img src={product.image} alt={product.name}/>
              <button onClick={() => removeFromCart(product)}>Remove</button>
            </div>
          ))}
      </div>
    </>
  )

  const loginPage = () => (
    <>
    <h1>Cart</h1>
        <div className="cart">
          {Cart.map((product, idx) => (
            <div className="Login">
            <form onSubmit={handleSubmit}>
              <FormGroup controlId="email" bsSize="large">
                <ControlLabel>Email</ControlLabel>
                <FormControl
                  autoFocus
                  type="email"
                  value={email}
                  onChange={e => setEmail(e.target.value)}
                />
              </FormGroup>
              <FormGroup controlId="password" bsSize="large">
                <ControlLabel>Password</ControlLabel>
                <FormControl
                  value={password}
                  onChange={e => setPassword(e.target.value)}
                  type="password"
                />
              </FormGroup>
              <Button block bsSize="large" disabled={!validateForm()} type="submit">
                Login
              </Button>
            </form>
          </div>
          ))}
      </div>
    </>
  )

  return (
    <div className="App">
      <header>
        <button onClick={() => navigateTo(PAGE_CART)}>Go to Cart({Cart.length})</button>

        <button onClick={() => navigateTo(PAGE_PRODUCTS)}>View Products</button>

        <button onClick={() => navigateTo(PAGE_PRODUCTS)}>Login</button>
      </header>
      {page === PAGE_PRODUCTS && renderProducts()}
      {page === PAGE_CART && renderCart()}
    </div>
  );
}

export default App;
